import textwrap
from .logging_utils import BOLD, BLUE, MAGENTA, CYAN, YELLOW, GREEN, RED, RESET

def show_effects_table():
    effects = [
        (1, "–ü–æ—Å—Ç–µ—Ä–∏–∑–∞—Ü–∏—è", "–ß–∏—Å—Ç–æ–µ —É–ø—Ä–æ—â–µ–Ω–∏–µ —Ü–≤–µ—Ç–æ–≤, –±–µ–∑ —Ñ–∏–ª—å—Ç—Ä–æ–≤.", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–ò—Å–ø–æ–ª—å–∑—É–π –º–∞–ª–æ–µ —á–∏—Å–ª–æ —Ü–≤–µ—Ç–æ–≤ –¥–ª—è –≥—Ä–∞—Ñ–∏—á–Ω—ã—Ö —Ä–µ–∑—É–ª—å—Ç–∞—Ç–æ–≤"),
        (2, "–ü–ª–∞–≤–Ω—ã–µ –ø—è—Ç–Ω–∞", "–ú—è–≥–∫–∏–µ –ø–µ—Ä–µ—Ö–æ–¥—ã, –≥–ª—è–Ω–µ—Ü.", "–°—Ä–µ–¥–Ω–µ", "–ü–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è –ø–æ—Ä—Ç—Ä–µ—Ç–æ–≤ ‚Äî —É–≤–µ–ª–∏—á—å bilateral –¥–ª—è –±–æ–ª–µ–µ –ø–ª–∞–≤–Ω–æ–≥–æ –±–ª–µ—Å–∫–∞"),
        (3, "–ö–æ–º–∏–∫—Å", "–ö–æ–Ω—Ç—É—Ä—ã + —Å–≥–ª–∞–∂–µ–Ω–Ω—ã–µ –ø—è—Ç–Ω–∞ ‚Äî —Ä–∏—Å—É–Ω–æ–∫ —Ç—É—à—å—é.", "–°—Ä–µ–¥–Ω–µ", "–£–º–µ–Ω—å—à–∏ —Ä–µ–∑–∫–æ—Å—Ç—å –∫–æ–Ω—Ç—É—Ä–æ–≤, –µ—Å–ª–∏ —Å–ª–∏—à–∫–æ–º –∞–≥—Ä–µ—Å—Å–∏–≤–Ω–æ"),
        (4, "–ë—É–º–∞–≥–∞ (Cutout)", "–í—ã—Ä–µ–∑–∞–Ω–Ω—ã–µ —Å–ª–æ–∏, –Ω–µ—Ä–æ–≤–Ω—ã–µ –∫—Ä–∞—è (–∫–∞–∫ –º–µ–ª/–Ω–æ–∂–Ω–∏—Ü—ã).", "–°—Ä–µ–¥–Ω–µ", "–ü–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è —Å–∏–ª—É—ç—Ç–æ–≤ ‚Äî –ø–æ–ø—Ä–æ–±—É–π —Ä–∞–∑–Ω–æ–µ –ø–æ—Ä–æ–≥–∞ Canny"),
        (5, "–°–µ—Ç–∫–∞", "–ü–æ–ª–æ–≤–∏–Ω–Ω–∞—è —Ç–æ–Ω–∏—Ä–æ–≤–∫–∞/—Ä—É–±—Ä–∏–∫–∞—Ü–∏—è ‚Äî —Ä–µ—Ç—Ä–æ-—à—Ç—Ä–∏—Ö–æ–≤–∫–∞.", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–®–∞–≥ —Å–µ—Ç–∫–∏ –≤–ª–∏—è–µ—Ç –Ω–∞ ¬´—Ç–æ–Ω¬ª"),
        (6, "–ü—ã–ª—å", "–ú—è–≥–∫–∏–π —à—É–º + —Ä–∞—Å—Å–µ—è–Ω–Ω—ã–π —Å–≤–µ—Ç –≤ —è—Ä–∫–∏—Ö –∑–æ–Ω–∞—Ö.", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–î–ª—è —ç—Ñ—Ñ–µ–∫—Ç–∞ ‚Äî —É–≤–µ–ª–∏—á—å —É—Ä–æ–≤–µ–Ω—å —à—É–º–∞ –∏ –ª—ë–≥–∫–∏–π blur"),
        (7, "–ú–µ–ª", "–†–∏—Å–æ–≤–∞–Ω–∏–µ –º–µ–ª–æ–º: –º—è–≥–∫–∏–µ, –Ω–µ–º–Ω–æ–≥–æ —Å—É—Ö–∏–µ –≥—Ä–∞–Ω–∏—Ü—ã.", "–ú–µ–¥–ª–µ–Ω–Ω–æ", "–ü–æ–∏–≥—Ä–∞–π —Å detail / –∫–æ–Ω—Ç—Ä–∞—Å—Ç–æ–º –¥–ª—è ¬´–ø—ã–ª—å–Ω–æ–≥–æ¬ª –º–µ–ª–∫–∞"),
        (8, "ASCII-–ê—Ä—Ç", "–¢–æ–ª—å–∫–æ —Å–∏–º–≤–æ–ª—ã! –¶–≤–µ—Ç–Ω–æ–π + –ß–ë –≤–µ—Ä—Å–∏—è.", "–°—Ä–µ–¥–Ω–µ", "100% ASCII, –±–µ–∑ —Ñ–æ–Ω–∞. –°–æ—Ö—Ä–∞–Ω—è–µ—Ç –ß–ë –æ—Ç–¥–µ–ª—å–Ω–æ"),
        (9, "–ü–ª–∞—Å—Ç–∏–∫", "–ì–ª—è–Ω—Ü–µ–≤—ã–π, –∏–≥—Ä—É—à–µ—á–Ω—ã–π, —è—Ä–∫–∏–µ –±–ª–∏–∫–∏.", "–°—Ä–µ–¥–Ω–µ", "–î–æ–±–∞–≤—å specular highlight mask –¥–ª—è —É—Å–∏–ª–µ–Ω–∏—è –±–ª–µ—Å–∫–∞"),
        (10, "–ì—Ä–∞—Ñ—Ñ–∏—Ç–∏", "–£–ª–∏—á–Ω—ã–π —Å—Ç–∏–ª—å: –∫—Ä–∞—Å–∫–∞ –∏–∑ –±–∞–ª–ª–æ–Ω—á–∏–∫–∞ –Ω–∞ —Å—Ç–µ–Ω–µ, —Å —Ñ–∞–∫—Ç—É—Ä–æ–π –∏ –∫–æ–Ω—Ç—Ä–∞—Å—Ç–æ–º.", "–°—Ä–µ–¥–Ω–µ", "–£–≤–µ–ª–∏—á—å –Ω–∞—Å—ã—â–µ–Ω–Ω–æ—Å—Ç—å –∏ –∑–µ—Ä–Ω–∏—Å—Ç–æ—Å—Ç—å –¥–ª—è —Ä–µ–∞–ª–∏—Å—Ç–∏—á–Ω–æ–π —Ç–µ–∫—Å—Ç—É—Ä—ã –±–µ—Ç–æ–Ω–∞"),
        (11, "–í–µ–∫—Ç–æ—Ä–∏–∑–∞—Ü–∏—è", "–ú–∏–Ω–∏–º–∞–ª–∏—Å—Ç–∏—á–Ω—ã–π –∞—Ä—Ç –∏–∑ –∫—Ä—É–ø–Ω—ã—Ö —Ü–≤–µ—Ç–Ω—ã—Ö –ø—è—Ç–µ–Ω.", "–°—Ä–µ–¥–Ω–µ", "–ö–æ–ª–∏—á–µ—Å—Ç–≤–æ —Ü–≤–µ—Ç–æ–≤ (K) –∑–∞–¥–∞—ë—Ç —É—Ä–æ–≤–µ–Ω—å —É–ø—Ä–æ—â–µ–Ω–∏—è ‚Äî –º–µ–Ω—å—à–µ = —á–∏—â–µ."),
        (12, "–°–≤–µ—á–∞", "–¢—ë–ø–ª–æ–µ –±–æ–∫–æ–≤–æ–µ –æ—Å–≤–µ—â–µ–Ω–∏–µ, –∑–æ–ª–æ—Ç–∏—Å—Ç—ã–µ —Ç–µ–Ω–∏.", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–ü–æ–¥–æ–π–¥—ë—Ç –¥–ª—è —Å—Ü–µ–Ω —Å —Ç—ë–ø–ª—ã–º –Ω–∞—Å—Ç—Ä–æ–µ–Ω–∏–µ–º"),
        (13, "–ì–æ–ª–æ–≥—Ä–∞–º–º–∞", "–†–∞–¥—É–∂–Ω—ã–µ –ø–µ—Ä–µ–ª–∏–≤—ã, –∫–∞–∫ –Ω–∞ CD-–¥–∏—Å–∫–µ.", "–û—á–µ–Ω—å –º–µ–¥–ª–µ–Ω–Ω–æ", "–ò—Å–ø–æ–ª—å–∑—É–µ—Ç —à—É–º, HSV-—Å–¥–≤–∏–≥ –∏ –∫–æ–ª–æ—Ä–º—ç–ø –¥–ª—è –±–ª–µ—Å—Ç—è—â–µ–≥–æ —ç—Ñ—Ñ–µ–∫—Ç–∞"),
        (14, "–ê–∫–≤–∞—Ä–µ–ª—å", "–ú—è–≥–∫–∏–µ —Ä–∞—Å—Ç–µ–∫–∞—é—â–∏–µ—Å—è –ø–µ—Ä–µ—Ö–æ–¥—ã, –∫–∞–∫ –∫–∏—Å—Ç—å –∏ –≤–æ–¥–∞.", "–ú–µ–¥–ª–µ–Ω–Ω–æ", "–£–±–∏—Ä–∞–µ–º —Ä–µ–∑–∫–∏–µ –∫–æ–Ω—Ç—É—Ä—ã, —Å—Ç–∞–≤–∏–º —Å–∏–ª—å–Ω—ã–π edge-preserve + blur"),
        (15, "–¢–µ—Ö–Ω–∏—á–µ—Å–∫–∏–π", "–ß–µ—Ä—Ç—ë–∂: –±–µ–ª—ã–µ –ª–∏–Ω–∏–∏ –Ω–∞ —Å–∏–Ω–µ–º —Ñ–æ–Ω–µ (blueprint).", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–õ–∏–Ω–∏–∏ ‚Äî –±–µ–ª—ã–µ, —Ñ–æ–Ω ‚Äî —Å–∏–Ω–∏–π; –∏–Ω–≤–µ—Ä—Ç–∏—Ä—É–µ–º –∫–∞–Ω–∞–ª—ã –ø—Ä–∞–≤–∏–ª—å–Ω–æ"),
        (16, "–ö–∞—Ä—Ç–∞", "–¢–æ–ø–æ–≥—Ä–∞—Ñ–∏—á–µ—Å–∫–∞—è –ø–∞–ª–∏—Ç—Ä–∞ ‚Äî —É—Ä–æ–≤–Ω–∏ –∫–∞–∫ –∫–∞—Ä—Ç–∞ –≤—ã—Å–æ—Ç.", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "–ü–æ–¥—Ö–æ–¥–∏—Ç –¥–ª—è –ø–µ–π–∑–∞–∂–µ–π/—Å–ø—É—Ç–Ω–∏–∫–∞"),
        (17, "–ú–æ–∑–∞–∏–∫–∞", "–ù–∞–±–æ—Ä —Å–ª—É—á–∞–π–Ω—ã—Ö —Ñ–∏–≥—É—Ä –≤ —Å–µ—Ç–∫–µ.", "–°—Ä–µ–¥–Ω–µ–µ", "–ß–µ–º –±–æ–ª—å—à–µ –∫–ª–µ—Ç–æ–∫ ‚Äî —Ç–µ–º –ø–ª–æ—Ç–Ω–µ–µ –º–æ–∑–∞–∏–∫–∞"),
        (18, "–°–æ–Ω", "–ü–ª—ã–≤—É—â–∞—è, —Å–ª–µ–≥–∫–∞ –∏—Å–∫–∞–∂—ë–Ω–Ω–∞—è —Å—Ç—Ä—É–∫—Ç—É—Ä–∞.", "–ú–µ–¥–ª–µ–Ω–Ω–æ", "–ò—Å–∫–∞–∂–µ–Ω–∏—è —Å–ª—É—á–∞–π–Ω—ã ‚Äî –±—É–¥—É—Ç —Ä–∞–∑–Ω—ã–µ –∫–∞–∂–¥—ã–π —Ä–∞–∑"),
        (19, "–ü–∏–∫—Å–µ–ª—å-–ê—Ä—Ç", "–ß–∏—Å—Ç–∞—è –ø–∏–∫—Å–µ–ª–∏–∑–∞—Ü–∏—è (—Ä–µ—Ç—Ä–æ).", "–û—á–µ–Ω—å –±—ã—Å—Ç—Ä–æ", "scale_factor ~0.05..0.2"),
        (20, "–û–≥–Ω–µ–Ω–Ω—ã–π", "–¢—ë–ø–ª–æ–µ —Å–≤–µ—á–∞/–ø–ª–∞–º—è ‚Äî –º—è–≥–∫–∏–π glow –≤ —Ç—ë–ø–ª—ã—Ö —Ç–æ–Ω–∞—Ö.", "–°—Ä–µ–¥–Ω–µ", "Glow –¥–µ–ª–∞–µ—Ç—Å—è –ø–æ —è—Ä–∫–æ—Å—Ç–Ω–æ–π –º–∞—Å–∫–µ, –Ω–µ –ø—Ä–æ—Å—Ç–æ –∫–æ–Ω—Ç—Ä–∞—Å—Ç"),
        (21, "–†–µ–Ω—Ç–≥–µ–Ω-–∫–æ–Ω—Ç—É—Ä", "–≠—Ñ—Ñ–µ–∫—Ç –∫–∞–∫ —Ä–µ–Ω—Ç–≥–µ–Ω-—Å–Ω–∏–º–æ–∫: —Ç–æ–Ω–∫–∏–µ –ª–∏–Ω–∏–∏, –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–Ω—ã–µ –¥–µ—Ç–∞–ª–∏, –º–æ–Ω–æ—Ö—Ä–æ–º —Å –∞–∫—Ü–µ–Ω—Ç–æ–º —Ü–≤–µ—Ç–æ–º.", "–°—Ä–µ–¥–Ω–µ", "–ò—Å–ø–æ–ª—å–∑—É–π –∏–Ω–≤–µ—Ä—Å–∏—é —è—Ä–∫–æ—Å—Ç–∏ –∏ –≤—ã–¥–µ–ª–µ–Ω–∏–µ —Ç–æ–Ω–∫–∏—Ö —Å—Ç—Ä—É–∫—Ç—É—Ä; —Ü–≤–µ—Ç–Ω–æ–π –∞–∫—Ü–µ–Ω—Ç –º–æ–∂–µ—Ç –ø–æ–¥—á–µ—Ä–∫–Ω—É—Ç—å –∫–æ–Ω—Ç—É—Ä—ã"),
        (22, "Graph", "–ì—Ä–∞—Ñ–∏—á–µ—Å–∫–∏–π —ç—Ñ—Ñ–µ–∫—Ç: –∫–æ–Ω—Ç—É—Ä—ã + –ø–æ–ª–∏–Ω–æ–º–∏–∞–ª—å–Ω–∞—è –∞–ø–ø—Ä–æ–∫—Å–∏–º–∞—Ü–∏—è (y = P(x)), –º–æ–Ω–æ—Ö—Ä–æ–º —Å —Ü–≤–µ—Ç–Ω—ã–º–∏ –∞–∫—Ü–µ–Ω—Ç–∞–º–∏.", "–°—Ä–µ–¥–Ω–µ", "–ò—Å–ø–æ–ª—å–∑—É–π –∏–Ω–≤–µ—Ä—Å–∏—é —è—Ä–∫–æ—Å—Ç–∏, CLAHE –∏ multi-scale –¥–µ—Ç–µ–∫—Ü–∏—é –∫—Ä–∞—ë–≤; —ç–∫—Å–ø–æ—Ä—Ç–∏—Ä—É–µ—Ç PNG –∏ TXT —Å —É—Ä–∞–≤–Ω–µ–Ω–∏—è–º–∏"),
        (23, "Vice City", "–¶–ï–ù–ó–£–†–ê.", "–ú–µ–¥–ª–µ–Ω–Ω–æ", "–¶–ï–ù–ó–£–†–ê."),
    ]

    w_num, w_name, w_desc, w_speed = 4, 20, 45, 18
    wrap_desc = 45
    wrap_hint = 40

    print(f"\n{CYAN}{'='*110}{RESET}")
    print(f"{BOLD}{BLUE}–°–ü–ò–°–û–ö –î–û–°–¢–£–ü–ù–´–• –≠–§–§–ï–ö–¢–û–í (—Å –ø–æ–¥—Å–∫–∞–∑–∫–∞–º–∏){RESET}")
    print(f"{CYAN}{'-'*110}{RESET}")

    header = f"{BOLD}{MAGENTA}{'‚Ññ':<{w_num}}{CYAN}{'–ù–∞–∑–≤–∞–Ω–∏–µ':<{w_name}}{YELLOW}{'–û–ø–∏—Å–∞–Ω–∏–µ':<{w_desc}}{GREEN}{'–°–∫–æ—Ä–æ—Å—Ç—å':<{w_speed}}{RESET}–ü–æ–¥—Å–∫–∞–∑–∫–∞"
    print(header)
    print(f"{CYAN}{'-'*110}{RESET}")

    for n, name, desc, speed, hint in effects:
        desc_lines = textwrap.wrap(desc, wrap_desc)
        hint_lines = textwrap.wrap(hint, wrap_hint)
        max_lines = max(len(desc_lines), len(hint_lines))

        # üî¥ –≤—ã–¥–µ–ª—è–µ–º "–¶–ï–ù–ó–£–†–£" –∫—Ä–∞—Å–Ω—ã–º —Ü–≤–µ—Ç–æ–º
        if "–¶–ï–ù–ó–£–†–ê" in name:
            name_fmt = f"{BOLD}{RED}{name:<{w_name}}{RESET}"
            desc_color = RED
            speed_color = RED
        else:
            name_fmt = f"{CYAN}{name:<{w_name}}{RESET}"
            desc_color = YELLOW
            speed_color = GREEN

        for i in range(max_lines):
            num_str = f"{BOLD}{MAGENTA}{n:<{w_num}}{RESET}" if i == 0 else " " * w_num
            name_str = name_fmt if i == 0 else " " * w_name
            desc_str = f"{desc_color}{desc_lines[i]:<{w_desc}}{RESET}" if i < len(desc_lines) else " " * w_desc
            speed_str = f"{speed_color}{speed:<{w_speed}}{RESET}" if i == 0 else " " * w_speed
            hint_str = hint_lines[i] if i < len(hint_lines) else ""
            print(f"{num_str}{name_str}{desc_str}{speed_str}{hint_str}")

    print(f"{CYAN}{'='*110}{RESET}\n")
